mapper.select:
SELECT * FROM hsgbiz_files
WHERE ID = #{id}
ORDER BY POST_DATE DESC

SELECT * FROM hsgbiz_files
WHERE ID = #{contentsId}
ORDER BY POST_DATE DESC

SELECT * FROM hsgbiz_files
WHERE ID = #{pageId}
ORDER BY POST_DATE DESC

mapper.update:
UPDATE hsgbiz_files
CONTENTS_ID = #{contentsId}, PAGE_ID = #{pageId}, ORIGIN = #{origin}, STORED = #{stored}, SIZE = #{size}, CATEGORY = #{category}, POST_DATE = #{postDate}
WHERE ID = #{id}
